UPDATE `creature_text` SET text = '¿Así que por fin ha llegado la elogiada justicia de la Luz? ¿Debería deponer la Agonía de Escarcha y confiar en tu piedad, Vadín?' WHERE text LIKE '%So%the%Light\'s%vaunted%justice%has%finally%arrived?%Shall%I%lay%down%Frostmourne%and%throw%myself%at%your%mercy,%Fordring?%';

UPDATE `creature_text` SET text = 'Te daremos una muerte rápida, Arthas. Más de lo que tuvieron aquellos a los que torturaste y asesinaste.' WHERE text LIKE '%We\'ll%grant%you%a%swift%death,%Arthas.%More%than%can%be%said%for%the%thousands%you\'ve%tortured%and%slain.%';

