UPDATE `creature_text` SET text = '¿Habéis venido para ajusticiar a Arthas? ¿Para ver la destrucción del Rey Exánime?' WHERE text LIKE '%You%have%come%to%bring%Arthas%to%justice?%To%see%the%Lich%King%destroyed?%';

UPDATE `creature_text` SET text = '¡Agonía de Escarcha! ¡Obedéceme!' WHERE text LIKE '%Argh...%Frostmourne,%obey%me!%';

