UPDATE `creature_text` SET text = ' Esta es nuestra batalla final. El paso del tiempo se hará eco de lo que aquí ocurra. Sin importar el resultado, sabrán que luchamos con honor. ¡Que luchamos por la libertad y la seguridad de nuestro pueblo!' WHERE text LIKE '%This%is%our%final%stand.%What%happens%here%will%echo%through%the%ages.%Regardless%of%outcome,%they%will%know%that%we%fought%with%honor.%That%we%fought%for%the%freedom%and%safety%of%our%people!%';

UPDATE `creature_text` SET text = 'Recordad, héroes, el miedo es el mayor enemigo en estas cámaras infectas. Fortaleced vuestro corazón y vuestra alma brillará más que mil soles. El enemigo vacilará al veros. ¡Caerán en cuanto la luz de la rectitud los envuelva!' WHERE text LIKE '%Remember,%heroes,%fear%is%your%greatest%enemy%in%these%befouled%halls.%Steel%your%heart%and%your%soul%will%shine%brighter%than%a%thousand%suns.%The%enemy%will%falter%at%the%sight%of%you.%They%will%fall%as%the%light%of%righteousness%envelops%them!%';

UPDATE `creature_text` SET text = '¡Marchemos hacia la Ciudadela de Corona de Hielo ahora!' WHERE text LIKE '%Our%march%upon%Icecrown%Citadel%begins%now!%';

UPDATE `creature_text` SET text = 'Ahora estás sobre la tierra sagrada de la Plaga. La Luz no te protegerá aquí, paladín. Nada te protegerá...' WHERE text LIKE '%You%now%stand%upon%the%hallowed%ground%of%the%Scourge.%The%Light%won\'t%protect%you%here,%paladin.%Nothing%will%protect%you...%';

UPDATE `creature_text` SET text = '¡ARTHAS! ¡Juré que te vería muerto y a la Plaga desmantelada! ¡Voy a acabar lo que comencé en la Esperanza de la Luz!' WHERE text LIKE '%ARTHAS!%I%swore%that%I%would%see%you%dead%and%the%Scourge%dismantled!%I\'m%going%to%finish%what%I%started%at%Light\'s%Hope!%';

UPDATE `creature_text` SET text = 'Podrías haber sido el mejor de mis campeones, Vadín: una fuerza oscura que asolaría el mundo y lo llevaría a una nueva era de conflictos.' WHERE text LIKE '%You%could%have%been%my%greatest%champion,%Fordring:%A%force%of%darkness%that%would%wash%over%this%world%and%deliver%it%into%a%new%age%of%strife.%';

UPDATE `creature_text` SET text = 'Pero ese honor ya no te pertenece. Pronto, tendré un nuevo campeón.' WHERE text LIKE '%But%that%honor%is%no%longer%yours.%Soon,%I%will%have%a%new%champion.%';

UPDATE `creature_text` SET text = 'El adiestramiento ha sido agotador. Las atrocidades que he cometido sobre su alma... ha resistido tanto tiempo, pero pronto se inclinará ante su Rey.' WHERE text LIKE '%The%breaking%of%this%one%has%been%taxing.%The%atrocities%I%have%committed%upon%his%soul.%He%has%resisted%for%so%long,%but%he%will%bow%down%before%his%king%soon.%';

UPDATE `creature_text` SET text = '¡NUNCA! Nunca... te... serviré...' WHERE text LIKE '%NEVER!%I...%I%will%never...%serve...%you...%';

UPDATE `creature_text` SET text = 'Al final, todos me serviréis.' WHERE text LIKE '%In%the%end,%you%will%all%serve%me.%';

UPDATE `creature_text` SET text = '¿El paladín aún vive? ¿Cómo es posible, Alto señor? ¿Cómo pudo sobrevivir?' WHERE text LIKE '%The%paladin%still%lives?%Is%it%possible,%High%Lord?%Could%he%have%survived?%';

UPDATE `creature_text` SET text = 'El poder de la Luz no tiene límites, Colmillosauro. Su alma está bajo una gran tensión, pero vive, por ahora.' WHERE text LIKE '%The%power%of%the%light%knows%no%bounds,%Saurfang.%His%soul%is%under%great%strain,%but%he%lives...%for%now.%';

UPDATE `creature_text` SET text = '¡Debemos salvarle! Si rescatamos a Bolvar Fordragón, quizá calmemos las tensiones entre la Alianza y la Horda.' WHERE text LIKE '%Then%we%must%save%him.%If%we%rescue%Bolvar%Fordragon%we%may%quell%the%unrest%between%the%Alliance%and%the%Horde.%';

UPDATE `creature_text` SET text = 'Nuestra misión está clara: el Rey Exánime responderá por sus crímenes y salvaremos al alto señor Bolvar Fordragón.' WHERE text LIKE '%Our%mission%is%now%clear.%The%Lich%King%will%answer%for%his%crimes,%and%we%will%save%Highlord%Bolvar%Fordragon.%';